sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	try {
		var se = new GlideRecord('service_entitlement');
		se.addQuery('contract', current.sys_id);
//this doesn't work, so aggregating with a loop
//		se.addAggregate('SUM', 'x_175353_contracts_contract_quantity*x_175353_contracts_contracted_price');
//		se.addAggregate('SUM', 'x_175353_contracts_provisioned_qty*x_175353_contracts_contracted_price');
//		se.setGroup(false);
		se.query();

		var cv = 0;
		var pv = 0;
		while (se.next()) {
			cv += se.x_175353_contracts_contract_quantity * se.x_175353_contracts_contracted_price;
			pv += se.x_175353_contracts_provisioned_qty * se.x_175353_contracts_contracted_price;
		}

		if (current.x_175353_contracts_contract_value != cv){
			current.x_175353_contracts_contract_value = cv;
		}
		if (current.x_175353_contracts_provisioned_value != pv){
			current.x_175353_contracts_provisioned_value = pv;
		}
	}
	catch(ex) {
		gs.addErrorMessage(ex.message);
	}
})(current, previous);
